UPDATE `broadcast_text` WHERE `entry`=4732 SET `male_text`='I am not here to harm you, Kharan. Gor\'shak sent me. He told me that you would speak to me about the Princess.', `female_text`='I am not here to harm you, Kharan. Gor\'shak sent me. He told me that you would speak to me about the Princess.';
