UPDATE `broadcast_text` WHERE `entry`=5025 SET `male_text`='Dwarves don\'t get drunk, $g mister:missy;. I\'m just a little sloppy.$B$BAnyhow, Windsor? I figure he\'s somewhere in Blackrock Depths. That\'s the Dark Iron city for you unedumecated peoples.', `female_text`='';
