UPDATE `broadcast_text` WHERE `entry`=6321 SET `male_text`='You\'re into leather, eh?  Sounds like you should talk to Simon Tanner over in Old Town.  He runs a shop there called the Protective Hide.', `female_text`='You\'re into leather, eh?  Sounds like you should talk to Simon Tanner over in Old Town.  He runs a shop there called the Protective Hide.';
