UPDATE `broadcast_text` WHERE `entry`=7040 SET `male_text`='A Thunderbrew ale and a bit of Gremlock\'s cooking makes for a nice meal indeed.  Ye really should hit him up for some training if ye want to be a cook.', `female_text`='A Thunderbrew ale and a bit of Gremlock\'s cooking makes for a nice meal indeed.  Ye really should hit him up for some training if ye want to be a cook.';
