UPDATE `broadcast_text` WHERE `entry`=7589 SET `male_text`='Ah, well aren\'t you a sturdy-looking one? Perhaps you can assist me with a thing or two. Not much help around here except for green apprentices, and they\'ve other things to worry about.', `female_text`='';
