UPDATE `broadcast_text` WHERE `entry`=7591 SET `male_text`='The land of Azshara.', `female_text`='The land of Azshara.';
UPDATE `broadcast_text` WHERE `entry`=7592 SET `male_text`='My knowledge of night elf lore is limited, but it is said long ago--back when the night elves still held sacred the art of arcane spell casting--that Azshara was the greatest of their kind. A beautiful woman they named queen. Sadly, it was her addiction to the arcane that would one day be the catalyst for the most devastating events in this world\'s history.', `female_text`='';
