UPDATE `broadcast_text` WHERE `entry`=10738 SET `male_text`='I\'ll tell you \'then what.\' She went runnin\' straight for the hive. And you know what\'s crazier? Not a single damned bug got in her way or tried to stop her. She ran through them all as if she\'d been best friends with \'em for centuries.', `female_text`='';
