UPDATE `broadcast_text` WHERE `entry`=10927 SET `male_text`='After the savagery that my people have witnessed and felt, you expect me to accept another burden, dragon? Surely you are mad.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=10928 SET `male_text`='I want nothing to do with Silithus, the Qiraji and least of all, any damned dragons!', `female_text`='';
