UPDATE `broadcast_text` WHERE `entry`=10931 SET `male_text`='Lord Staghelm, where are you going? You would shatter our bond for the sake of pride?\n', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=10932 SET `male_text`='My son\'s soul will find no comfort in this hollow victory, dragon. I will have him back. Though it takes a millennia, I WILL have my son back!  ', `female_text`='';
