UPDATE `broadcast_text` WHERE `entry`=11275 SET `male_text`='You\'re just in time, $c.  Do you feel the ground shaking beneath you?$B$BSomething approaches from the hive.  Take a place near the grunts, $n.  I\'m counting on you!', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11276 SET `male_text`='The silithid are no match for the sons and daughters of Orgrimmar.  Soon all that will be left of them are their crushed limbs and wings under our feet, $n.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11277 SET `male_text`='Eranikus, Tyrant of the Dream, has entered our world.', `female_text`='Eranikus, Tyrant of the Dream, has entered our world.';
UPDATE `broadcast_text` WHERE `entry`=11278 SET `male_text`='', `female_text`='Very well, $n. I will use the Nightmare object to conjure forth Eranikus.';
UPDATE `broadcast_text` WHERE `entry`=11279 SET `male_text`='', `female_text`='Follow me.';
UPDATE `broadcast_text` WHERE `entry`=11280 SET `male_text`='', `female_text`='Steel yourselves! Summoning a being this powerful is seldom done with positive results.';
UPDATE `broadcast_text` WHERE `entry`=11281 SET `male_text`='', `female_text`='Eranikus is here! Seek shelter! Safety!';
UPDATE `broadcast_text` WHERE `entry`=11282 SET `male_text`='We will locate the origin of the Nightmare through the fragments you collected, $n. From there we will pull Eranikus through a rift in the Dream. Steel yourself, $c. We are inviting the embodiment of the Nightmare into our world.', `female_text`='';
