UPDATE `broadcast_text` WHERE `entry`=11290 SET `male_text`='The rift will be opened there, above Lake Elun\'ara. Prepare yourself, $n. Eranikus\'s entry into our world will be wrought with chaos and strife.\n', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11291 SET `male_text`='He will stop at nothing to get to Malfurion\'s physical manifestation. That must not happen... We must keep the beast occupied long enough for Tyrande to arrive.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11292 SET `male_text`='Defend Nighthaven, hero...', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11293 SET `male_text`='Fiend! Face the might of Cenarius!', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11294 SET `male_text`='You are certainly not your father, insect. Should it interest me, I would crush you with but a swipe of my claws. Turn Shan\'do Stormrage over to me and your pitiful life will be spared along with the lives of your people.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11295 SET `male_text`='Who is the predictable one, beast? Surely you did not think that we would summon you on top of Malfurion? Your redemption comes, Eranikus. You will be cleansed of this madness - this corruption.\n', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11296 SET `male_text`='%s lets loose a sinister laugh.', `female_text`='';
