UPDATE `broadcast_text` WHERE `entry`=11309 SET `male_text`='', `female_text`='Elune, hear my prayers. Grant us serenity! Watch over our fallen...';
UPDATE `broadcast_text` WHERE `entry`=11310 SET `male_text`='', `female_text`='Seek absolution, Eranikus. All will be forgiven...\n';
UPDATE `broadcast_text` WHERE `entry`=11311 SET `male_text`='', `female_text`='You will be forgiven, Eranikus. Elune will always love you. Break free of the bonds that command you!';
UPDATE `broadcast_text` WHERE `entry`=11312 SET `male_text`='', `female_text`='The grasp of the Old Gods is unmoving. He is consumed by their dark thoughts... I... I cannot... cannot channel much longer... Elune aid me...';
UPDATE `broadcast_text` WHERE `entry`=11313 SET `male_text`='Eranikus, Tyrant of the Dream, is wholly consumed by the Light of Elune. Tranquility sets in over the Moonglade.', `female_text`='Eranikus, Tyrant of the Dream, is wholly consumed by the Light of Elune. Tranquility sets in over the Moonglade.';
UPDATE `broadcast_text` WHERE `entry`=11314 SET `male_text`='IT BURNS! THE PAIN... SEARING...', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11315 SET `male_text`='WHY? Why did this happen to ... to me? Where were you Tyrande? Where were you when I fell from the grace of Elune?', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11316 SET `male_text`='I... I feel... I feel the touch of Elune upon my being once more... She smiles upon me... Yes... I...', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11317 SET `male_text`='', `female_text`='Tend to the injuries of the wounded, sisters!';
