UPDATE `broadcast_text` WHERE `entry`=11349 SET `male_text`='Around this reindeer\'s neck is a tag with the Smokywood Pastures logo on it and a name - \"Metzen\".$B$BMetzen looks at you with sad, forlorn eyes.  It is almost as if the reindeer has been immobilized by some unseen force.', `female_text`='';
