UPDATE `broadcast_text` WHERE `entry`=11862 SET `male_text`='Very clever how you got in here like that, $n.$B$BWhat? Surprised that I know your name? I\'m a demon, that\'s what we do. I also know your true name, so you\'d better buy something expensive.', `female_text`='';
