UPDATE `broadcast_text` WHERE `entry`=11896 SET `male_text`='It was not my intention to get you involved in this, $n.  I\'ve spent every waking hour looking over my shoulder ever since I helped an old friend in a daring raid on Blackrock Spire.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11897 SET `male_text`='Trying to find out more about these creatures hasn\'t been easy.  My friend and the person who hired his mercenaries to steal Lord Valthalak\'s spellbook have both been feared dead for a while.', `female_text`='';
