UPDATE `broadcast_text` WHERE `entry`=11903 SET `male_text`='Thrall has shown great kindness in granting me sanctuary within his domain.  I do not wish to overstay my welcome, at any rate.  It would be a great dishonor if I brought any trouble to our leader.', `female_text`='';
UPDATE `broadcast_text` WHERE `entry`=11904 SET `male_text`='', `female_text`='Try not to draw too much attention, I\'m waiting for the King to grant me audience.  Not that I mind waiting, I feel pretty safe around all these burly guards.';
