UPDATE `broadcast_text` WHERE `entry`=11912 SET `male_text`='I guess it\'s all just about over, right $n? I\'m going to miss you... it\'ll be pretty lonely up here without someone to talk to.$B$BBut that doesn\'t mean that you can\'t come back to see me in the future!', `female_text`='';
